// src/components/ProductCard.jsx
import React from "react";

function ProductCard({ product }) {
  return (
    <div className="w-40 sm:w-44 md:w-48 bg-white rounded-xl shadow-md overflow-hidden hover:shadow-lg transition-transform transform hover:scale-105">
      {/* Product Image */}
      <img
        src={product.image}
        alt={product.name}
        className="w-full h-36 object-cover rounded-t-xl"
        loading="lazy"
      />

      {/* Product Info */}
      <div className="p-3 text-center">
        <h3 className="text-sm font-semibold text-gray-800">{product.name}</h3>
        <p className="text-green-600 font-bold mt-1 text-sm">â‚¹{product.price}</p>
      </div>
    </div>
  );
}

export default ProductCard;
