import React from "react";

const ProductCard = ({ product }) => {
  return (
    <div className="bg-white rounded-xl shadow-md p-3 w-[160px] hover:shadow-lg transition-all duration-200 text-center">
      <div className="w-[100px] h-[100px] mx-auto overflow-hidden rounded-md border border-gray-200">
        <img
          src={product.image}
          alt={product.name}
          className="w-full h-full object-cover object-center"
        />
      </div>
      <h3 className="text-sm font-semibold text-gray-800 mt-2">{product.name}</h3>
      <p className="text-xs text-green-700 font-medium">{product.price}</p>
    </div>
  );
};

export default ProductCard;
