// src/components/ProductCard.jsx
import React from "react";

function ProductCard({ product }) {
  return (
    <div className="border rounded-xl p-3 shadow-md hover:shadow-lg transition-transform transform hover:scale-105 w-40">
      {/* ðŸ–¼ Smaller image */}
      <img
        src={product.image}
        alt={product.name}
        className="w-16 h- object-contain mx-auto"
      />
      <h2 className="text-sm font-semibold mt-2 text-center">{product.name}</h2>
      <p className="text-gray-600 text-xs text-center">${product.price}</p>
    </div>
  );
}

export default ProductCard;
