import React from 'react';
import { useDispatch } from 'react-redux';
import { addToCart } from '../features/cart/cartSlice';

const ProductCard = ({ product }) => {
  const dispatch = useDispatch();

  return (
    <div className="bg-white rounded-2xl shadow-md p-4 text-center hover:scale-105 transition">
      <img src={product.image} alt={product.name} className="h-40 w-full object-cover rounded-lg mb-3" />
      <h2 className="text-lg font-semibold">{product.name}</h2>
      <p className="text-gray-500 mb-2">{product.category}</p>
      <p className="text-green-700 font-bold mb-3">â‚¹{product.price}</p>
      <button
        onClick={() => dispatch(addToCart(product))}
        className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-800 transition"
      >
        Add to Cart
      </button>
    </div>
  );
};

export default ProductCard;
