import React from "react";

const ProductCard = ({ product }) => {
  return (
    <div className="bg-white rounded-xl shadow-md p-3 w-[160px] hover:shadow-lg transition-all duration-200 text-center">
      <img
        src={product.image}
        alt={product.name}
        className="w-full h-28 object-cover object-center rounded-md mb-2"
      />
      <h3 className="text-sm font-semibold text-gray-800">{product.name}</h3>
      <p className="text-xs text-green-700 font-medium">{product.price}</p>
    </div>
  );
};

export default ProductCard;
