// src/components/ProductCard.jsx
import React from "react";

function ProductCard({ product }) {
  return (
    <div className="border rounded-lg p-3 shadow-md hover:shadow-lg transition-transform transform hover:scale-105 w-32 sm:w-36 md:w-40 bg-white">
      {/* üåø Small and centered image */}
      <div className="flex justify-center items-center">
        <img
          src={product.image}
          alt={product.name}
          className="w-20 h-20 object-contain"
          loading="lazy"
        />
      </div>

      {/* üè∑ Product info */}
      <h2 className="text-sm font-semibold mt-2 text-center text-gray-800">
        {product.name}
      </h2>
      <p className="text-green-600 text-xs text-center font-medium">
        ‚Çπ{product.price}
      </p>
    </div>
  );
}

export default ProductCard;
